import React from 'react';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios'

const StripeCheckoutButton = ({price}) => {
    const priceForStripe = price * 145
    const publishableKey = 'pk_test_WLxkBgQw50T0KgoPLnQmbxy5006bXWMTPQ'


    const onToken = token => {
        axios({
          url: 'payment',
          method:'post',
          data: {
            amount : priceForStripe,
            token
          }
        }).then(response => {
          alert('Payment successful')
        }).catch (error => {
          console.log(('Payment error: ', JSON.parse(error)))
          alert('There was an issue with your payment. Please make sure you use the provided card details');

        })
    }
    return (
      <StripeCheckout
        label="Pay Now"
        name="clothing-app"
        billingAddress
        shippingAddress
        image="https://sendeyo.com/up/d/f3eb2117da"
        description={`Your total is £${price}`} amount = {priceForStripe}
        panelLabel='Pay Now'
        token={onToken}
        stripeKey={publishableKey}
      />
    );
}

export default StripeCheckoutButton;
