import React from 'react'
import {Route } from 'react-router-dom'
import CollectionsOveriew from '../../collections-overview/collections-overview.component'
import {createStructuredSelector} from 'reselect'
import CollectionsPage from '../collection/collection.component'
import {firestore, convertCollectionsSnapshotToMap} from '../../../firebase/firebase.utils'
import {connect} from 'react-redux';
import { fetchCollectionsStart } from "../../../redux/shop/shop.actions";
import WithSpinner from '../../with-spinner/with-spinner.component'


const CollectionsOveriewWithSpinner = WithSpinner(CollectionsOveriew);
const CollectionPageWithSpinner = WithSpinner(CollectionsPage)

class ShopPage extends React.Component {
  state ={
    loading : true
  };

unsubscribeFromSnapShot = null;

componentDidMount(){
  const {updateCollections} = this.props
const collectionRef = firestore.collection('collections');

this.unsubscribeFromSnapShot = collectionRef.onSnapshot(async snapshot => {
  const collectionsMap = convertCollectionsSnapshotToMap(snapshot)
  updateCollections(collectionsMap);
  this.setState({loading: false});
})
}
render (){
  const {match} = this.props
  const {loading} = this.state
return (
  <div className="shop-page">
    <Route exact path ={`${match.path}`} render ={(props)=> <CollectionsOveriewWithSpinner isLoading={this.state} {...props} /> } />
    <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionPageWithSpinner isLoading= {loading} {...props}/>}/>
  </div>
);
}
}


const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStart: (collectionsMap) =>
    dispatch(updateCollections(collectionsMap)),
});


export default connect(null, mapDispatchToProps)(ShopPage)
